import React, { useState } from 'react';
import axios from 'axios';
import { useDropzone } from 'react-dropzone';
import './productUpload.css';

const apiUrl = 'http://localhost:5000/api/products'; // Replace with your backend API URL

const ProductUpload = () => {
    const [productName, setProductName] = useState('');
    const [productId, setProductId] = useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');
    const [image, setImage] = useState(null);
    const [message, setMessage] = useState('');
    const [messageType, setMessageType] = useState('');

    const onDrop = (acceptedFiles) => {
        setImage(acceptedFiles[0]);
    };

    const { getRootProps, getInputProps } = useDropzone({ onDrop, accept: 'image/*' });

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!productName || !productId || !price || !description || !image) {
            setMessage('All fields are required, including an image.');
            setMessageType('error');
            return;
        }

        const formData = new FormData();
        formData.append('name', productName);
        formData.append('productId', productId);
        formData.append('price', price);
        formData.append('description', description);
        formData.append('image', image);

        try {
            const response = await axios.post(apiUrl, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            setMessage('Product uploaded successfully!');
            setMessageType('success');
        } catch (error) {
            if (error.response && error.response.data) {
                setMessage(error.response.data.message || 'Error uploading product');
            } else {
                setMessage('Error uploading product');
            }
            setMessageType('error');
        }
    };

    return (
        <div className='full-screen'>
            <div className='small-screen'>
                <h2 className='header'>Product Upload Form</h2>
                <form onSubmit={handleSubmit} className='form'>
                    <div className='row'>
                        <div className='input-group'>
                            <label>Product Name:</label>
                            <input
                                type="text"
                                value={productName}
                                onChange={(e) => setProductName(e.target.value)}
                                required
                            />
                        </div>
                        <div className='input-group'>
                            <label>Product ID:</label>
                            <input
                                type="text"
                                value={productId}
                                onChange={(e) => setProductId(e.target.value)}
                                required
                            />
                        </div>
                    </div>
                    <div className='row'>
                        <div className='input-group'>
                            <label>Price:</label>
                            <input
                                type="number"
                                value={price}
                                onChange={(e) => setPrice(e.target.value)}
                                required
                            />
                        </div>
                    </div>
                    <div className='row full-width'>
                        <div className='input-group full-width'>
                            <label>Description:</label>
                            <textarea
                                value={description}
                                onChange={(e) => setDescription(e.target.value)}
                                required
                            />
                        </div>
                    </div>
                    <div {...getRootProps()} className='dropzone'>
                        <input {...getInputProps()} />
                        {image ? (
                            <p>{image.name}</p>
                        ) : (
                            <p>Drag & drop a product image here, or click to select one</p>
                        )}
                    </div>
                    <button type="submit" className='submit-button'>Submit</button>
                </form>
                {message && (
                    <p className={`message ${messageType}`}>{message}</p>
                )}
            </div>
        </div>
    );
};

export default ProductUpload;
