import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import './productUpload.css';

const ProductUpload = () => {
    const [productName, setProductName] = useState('');
    const [productId, setProductId] = useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');
    const [image, setImage] = useState(null);
    const [message, setMessage] = useState(''); // State for messages
    const [messageType, setMessageType] = useState(''); // State for message type (success/error)

    // Update this URL to match your backend endpoint
    const apiUrl = 'http://localhost:5000/api/products';

    const onDrop = (acceptedFiles) => {
        setImage(acceptedFiles[0]);
    };

    const { getRootProps, getInputProps } = useDropzone({ onDrop });

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append('name', productName);
        formData.append('prod_id', productId); // Adjust field name if necessary
        formData.append('price', price);
        formData.append('description', description);
        if (image) formData.append('image', image);

        try {
            const response = await axios.post(apiUrl, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            setMessage('Product uploaded successfully!');
            setMessageType('success'); // Set message type to success
        } catch (error) {
            console.error('Error uploading product:', error);

            let errorMessage = 'Error uploading product. Please try again.';
            if (error.response && error.response.data) {
                // Assuming backend sends error messages in `error.response.data.message`
                const backendMessage = error.response.data.message;
                if (backendMessage.includes('Image')) {
                    errorMessage = 'Image is required. Please upload an image.';
                } else if (backendMessage.includes('ID')) {
                    errorMessage = 'Invalid Product ID. Please check and try again.';
                } else if (backendMessage.includes('Name')) {
                    errorMessage = 'Product Name is required. Please provide a valid name.';
                } else if (backendMessage.includes('Price')) {
                    errorMessage = 'Price is required and must be a number. Please check and try again.';
                } else {
                    errorMessage = backendMessage; // Use backend-provided message
                }
            }
            setMessage(errorMessage);
            setMessageType('error'); // Set message type to error
        }
    };

    return (
        <div className='full-screen'>
            <div className='small-screen'>
                <h2 className='header'>Product Upload Form</h2>

                <form onSubmit={handleSubmit} className='form'>
                    <div className='row'>
                        <div className='input-group'>
                            <label>Product Name:</label>
                            <input
                                type="text"
                                value={productName}
                                onChange={(e) => setProductName(e.target.value)}
                                required
                            />
                        </div>
                        <div className='input-group'>
                            <label>Product ID:</label>
                            <input
                                type="text"
                                value={productId}
                                onChange={(e) => setProductId(e.target.value)}
                                required
                            />
                        </div>
                    </div>

                    <div className='row'>
                        <div className='input-group'>
                            <label>Price:</label>
                            <input
                                type="number"
                                value={price}
                                onChange={(e) => setPrice(e.target.value)}
                                required
                            />
                        </div>
                    </div>

                    <div className='row full-width'>
                        <div className='input-group full-width'>
                            <label>Description:</label>
                            <textarea
                                value={description}
                                onChange={(e) => setDescription(e.target.value)}
                                required
                            />
                        </div>
                    </div>

                    <div {...getRootProps()} className='dropzone'>
                        <input {...getInputProps()} />
                        {image ? (
                            <p>{image.name}</p>
                        ) : (
                            <p>Drag & drop a product image here, or click to select one</p>
                        )}
                    </div>
                    <button type="submit" className='submit-button'>Submit</button>
                </form>

                {/* Display message */}
                {message && (
                    <p className={`message ${messageType}`}>
                        {message}
                    </p>
                )}
            </div>
        </div>
    );
}

export default ProductUpload;
