import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import './productUpload.css';

// Replace with your API base URL
const apiUrl = 'http://localhost:5000/api/products';

const ProductUpload = () => {
    const [productName, setProductName] = useState('');
    const [productId, setProductId] = useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');
    const [image, setImage] = useState(null);
    const [images, setImages] = useState([]);
    const [message, setMessage] = useState('');

    const onDrop = (acceptedFiles) => {
        setImage(acceptedFiles[0]);
    };

    const onDropMultiple = (acceptedFiles) => {
        setImages(acceptedFiles);
    };

    const { getRootProps, getInputProps } = useDropzone({ onDrop });
    const { getRootProps: getRootPropsMultiple, getInputProps: getInputPropsMultiple } = useDropzone({ onDrop: onDropMultiple, multiple: true });

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!productName || !productId || !price || !description) {
            setMessage({ type: 'error', text: 'All fields are required.' });
            return;
        }

        if (!image) {
            setMessage({ type: 'error', text: 'Primary image is required.' });
            return;
        }

        const formData = new FormData();
        formData.append('name', productName);
        formData.append('productId', productId);
        formData.append('price', price);
        formData.append('description', description);
        formData.append('image', image);

        // Append multiple images
        images.forEach((img) => {
            formData.append('images', img);
        });

        try {
            const response = await axios.post(apiUrl, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            setMessage({ type: 'success', text: 'Product uploaded successfully!' });
        } catch (error) {
            if (error.response && error.response.data) {
                setMessage({ type: 'error', text: error.response.data.message || 'Error uploading product' });
            } else {
                setMessage({ type: 'error', text: 'Error uploading product' });
            }
        }
    };

    return (
        <div className='full-screen'>
            <div className='small-screen'>
                <h2 className='header'>Product Upload Form</h2>

                <form onSubmit={handleSubmit} className='form'>
                    <div className='row'>
                        <div className='input-group'>
                            <label>Product Name:</label>
                            <input
                                type="text"
                                value={productName}
                                onChange={(e) => setProductName(e.target.value)}
                                required
                            />
                        </div>
                        <div className='input-group'>
                            <label>Product ID:</label>
                            <input
                                type="text"
                                value={productId}
                                onChange={(e) => setProductId(e.target.value)}
                                required
                            />
                        </div>
                    </div>

                    <div className='row'>
                        <div className='input-group'>
                            <label>Price:</label>
                            <input
                                type="number"
                                value={price}
                                onChange={(e) => setPrice(e.target.value)}
                                required
                            />
                        </div>
                    </div>

                    <div className='row full-width'>
                        <div className='input-group full-width'>
                            <label>Description:</label>
                            <textarea
                                value={description}
                                onChange={(e) => setDescription(e.target.value)}
                                required
                            />
                        </div>
                    </div>

                    <div {...getRootProps()} className='dropzone'>
                        <input {...getInputProps()} />
                        {image ? (
                            <p>{image.name}</p>
                        ) : (
                            <p>Drag & drop a primary product image here, or click to select one</p>
                        )}
                    </div>

                    <div {...getRootPropsMultiple()} className='dropzone'>
                        <input {...getInputPropsMultiple()} />
                        {images.length > 0 ? (
                            <p>{images.map(img => img.name).join(', ')}</p>
                        ) : (
                            <p>Drag & drop additional product images here, or click to select multiple</p>
                        )}
                    </div>

                    <button type="submit" className='submit-button'>Submit</button>

                    {message && (
                        <div className={`message ${message.type}`}>
                            {message.text}
                        </div>
                    )}
                </form>
            </div>
        </div>
    );
}

export default ProductUpload;
